-- Create query including column "is_returning" to categorize customer based on number of transactions.
-- If more than one transaction, the value is 1. If not, then 0.
SELECT * FROM customers;
SELECT * FROM orders;
-- Check if the number of transaction is true
SELECT CustomerID, COUNT(CustomerID) as banyak_pembelian FROM orders
GROUP BY CustomerID;
-- return is_returning
SELECT CustomerID, COUNT(CustomerID) as banyak_pembelian,
CASE
	WHEN COUNT(CustomerID) > 1 then 1
	else 0
END AS is_returning FROM orders
GROUP BY CustomerID
ORDER BY CustomerID;

-- How many customers are returning to make transactions?
WITH CTE AS (SELECT CustomerID, COUNT(CustomerID) as banyak_pembelian,
CASE
	WHEN COUNT(CustomerID) > 1 then 1
	else 0
END AS is_returning FROM orders
GROUP BY CustomerID
ORDER BY CustomerID)

SELECT COUNT(is_returning) FROM CTE
WHERE is_returning = 1;